INSERT INTO `ps_category_lang` (`id_category`, `id_shop`, `id_lang`, `name`, `description`, `link_rewrite`, `meta_title`, `meta_keywords`, `meta_description`) VALUES
(1, 1, 1, 'Racine', '', 'racine', '', '', ''),
(2, 1, 1, 'Accueil', '', 'accueil', '', '', ''),
(3, 1, 1, 'Carte', '<p><strong>Vous trouverez ici tous les produits de notre restaurant.</strong></p>', 'carte', 'Carte du restaurant', 'california,sushi,temaki,yakitori,maki,chirashi,eggs,rolls,sashimi,ice cube', 'Retrouvez toutes les spécialités japonaise dans la carte de notre restaurant.'),
(4, 1, 1, 'Californias', '<p>Les Californias FAST SUSHI, à l''inverse de nos Makis sont</p>\r\n<p>élaborés avec la feuille d''algues à l''intérieur du rouleau,</p>\r\n<p>tandis que le riz l''entoure.</p>', 'californias', 'Californias', 'california,saumon,fromage,thon cuit,crevette,avocat,cheese,surimi,poulet,mayonnaise,cheese,chevre,poulet mayonnaise,saumon cheese,crevette avocat,thon,saumon fromage', 'Les Californias FAST SUSHI.'),
(8, 1, 1, 'Chirashis', '<p>Les Bols de Riz à Sushi FAST SUSHI et leurs</p>\r\n<p>accompagnements :</p>\r\n<ul><li>Saumon, Saumon Avocat, Thon, Thon Avocat, Thon Saumon,</li>\r\n<li>Assortiment.</li>\r\n</ul>', 'chirashis', 'Chirashis', 'chirashi,sushi,saumon,avocat,thon,assortiment,saumon avocat,thon avocat,thon saumon', 'Les Chirashis FAST SUSHI.'),
(12, 1, 1, 'Menus', '<p>Les menus sont servis avec :</p>\r\n<ul><li>1 <strong>salade de choux + 1 riz ou soupe Miso.</strong></li>\r\n</ul>', 'menus', 'Menus du restaurant', 'menu,plateau,midi,gourmand,yakitori,kikujiro,shogun,aibiki,sushi,maki,california,spring rolls,spring,rolls,sashimi,saumon,boeuf,boeuf fromage,poulet,boulettes poulet,thon,saumon cheese,cheese,concombre', 'Venez goutter nos  menus japonnais. Adaptés à toutes les faims ils vous apporteront une touche d''Asie dans l''assiette. '),
(13, 1, 1, 'Eggs / Rolls', '<p>Makis revisités FAST SUSHI :</p>\r\n<ul><li>Saumon, Thon cuit Avocat, Saumon Avocat,</li>\r\n<li>Poulet Mayonnaise,</li>\r\n<li>Cheese, Avocat, Avocat Cheese, Concombre Cheese.</li>\r\n</ul>', 'eggs-rolls', 'Eggs-Rolls', 'egg,roll,egg roll,saumon,thon,thon cuit,avocat,poulet,cheese,concombre,mayonnaise,poulet mayonnaise,avocat cheese,concombre cheese,saumon avocat', 'Les Eggs-Rolls FAST SUSHI.'),
(14, 1, 1, 'Entrées', '<p>Toutes les Entrées FAST SUSHI :</p>\r\n<ul><li>Gyoza Raviolis Japonais, </li>\r\n<li>Tartare de Saumon et Avocat,</li>\r\n<li>Salade Choux, Salade Choux Crevettes,</li>\r\n<li>Riz vinaigré, Soupe Miso, ...</li>\r\n</ul>', 'entrees', 'Entrees Fast Sushi', 'soupe miso,salade choux,salade choux crevettes,salade choux saumon,riz nature,riz vinaigre,tartare saumon avocat,gyoza raviolis japonais,gyoza,tartare saumon,salade,riz,saumon,avocat,crevette,choux,tartare,raviolis japonais,raviolis', 'Les entrées de Sast Sushi.'),
(15, 1, 1, 'Makis', '<p>Les algues séchées autour du riz et du vinaigre de riz</p>\r\n<p>sucré composent de savoureux Makis FAST SUSHI :</p>\r\n<ul><li>Saumon, Saumon Fromage, Saumon Fumé, </li>\r\n<li>Thon, Thon cuit, Surimi, Surimi Fromage, </li>\r\n<li>Avocat, Concombre, ...</li>\r\n</ul>', 'makis', 'Makis Sushi Fast.', 'sushi,makis,maki,saumon,avocat,fromage,surimi,thon,thon cuit,crevette,concombre,omelette,saumon fumé', 'Les Makis Sushi Fast.'),
(16, 1, 1, 'Sashimis', '<p>Goûtez nos Sashimis FAST SUSHI, Poissons crus taillés</p>\r\n<p>en tranches :</p>\r\n<ul><li>Saumon, Daurade, Thon,</li>\r\n<li>Assortiment, Double.</li>\r\n</ul>', 'sashimis', 'Sashimis FAST SUSHI.', 'sashimis,saumon,daurade,thon,assortiment,double', 'Les Sashimis de FAST SUSHI.'),
(17, 1, 1, 'Spring Rolls', '<p>Les Rouleaux de Printemps aux multiples saveurs</p>\r\n<p>SUSHI FAST :</p>\r\n<ul><li>Saumon Avocat, Thon cuit Avocat, </li>\r\n<li>Poulet Avocat Mayonnaise,</li>\r\n<li>Avocat Cheese, ...</li>\r\n</ul>', 'spring-rolls', 'Spring Rolls ', 'spring rolls,saumon avocat,saumon,avocat,thon cuit,thon avocat,thon,poulet mayonnaise,avocat mayonnaise,crevette avocat,poulet avocat,crevette mayonnaise,crevette,poulet,avocat cheese,concombre cheese,concombre,saumon cheese,avocat cheese', 'Les Spring-Rolls SUSHI FAST.'),
(18, 1, 1, 'Sushis', '<p>Composés de riz vinaigrés et de poissons crus,</p>\r\n<p>laissez-vous tenter par les Sushis FAST SUSHI :</p>\r\n<ul><li>Saumon, Saumon Cheese, Saumon Fumé Cheese,</li>\r\n<li>Daurade, Thon, Crevette, Saint-Jacques, </li>\r\n<li>Omelette, Tartare Thon, Tartare Saumon, ...</li>\r\n</ul>', 'sushis', 'Sushis', 'sushi,sushis,saumon,daurade,thon,saumon avocat,avocat,crevette,saumon cheese,cheese,saumon,saumon fumé cheese,saumon fumé,omelette,saint-jacques,tartare,tartare thon,tartare saumon', 'Les Sushis FAST SUSHI.'),
(19, 1, 1, 'Temakis', '<p>Enrobés d''algues grillées, craquez pour les Temakis</p>\r\n<p>FAST SUSHI: </p>\r\n<ul><li>Saumon Avocat, Thon Avocat,</li>\r\n<li>Surimi Avocat, Crevette Avocat.</li>\r\n</ul>', 'temakis', 'Temakis', 'temakis,sushi,saumon avocat,saumon,avocat,thon avocat,thon,surimi avocat,surimi,crevette avocat,crevette', 'Les Temakis FAST SUSHI.'),
(20, 1, 1, 'Eggs', '<p>Makis revisités FAST SUSHI :</p>\r\n<ul><li>Saumon, Thon cuit Avocat, Saumon Avocat,</li>\r\n<li>Cheese.</li>\r\n</ul>', 'eggs', 'Les Eggs FAST SUSHI.', 'egg,sushi,eggs,thon cuit,saumon avocat,thon cuit avocat,thon,saumon,avocat,cheese', 'Les Eggs FAST SUSHI.'),
(21, 1, 1, 'Rolls', '<p>Makis revisités FAST SUSHI :</p>\r\n<ul><li>Thon cuit, Saumon Avocat,</li>\r\n<li>Cheese, Avocat, Avocat Cheese, Concombre Cheese.</li>\r\n</ul>', 'rolls', 'Les Rolls FAST SUSHI.', 'rolls,thon cuit,thon,avocat,saumon,saumon avocat,cheese,concombre,concombre cheese,cheese,avocat cheese', 'Les Rolls FAST SUSHI.'),
(22, 1, 1, 'Ice Cubes', '<p>Découvrez les Ice Cubes FAST SUSHI :</p>\r\n<ul><li>Poulet Mayonnaise, Dinde Cheese, Boeuf, Boeuf au Fromage,</li>\r\n<li>Surimi ,Saumon Avocat ,Saumon Cheese, Thon, Thon cuit,</li>\r\n<li>Concombre Cheese.</li>\r\n</ul>', 'ice-cubes', 'Les Ice Cubes FAST SUSHI.', 'ice cubes,poulet mayonnaise,poulet,mayonnaise,dinde cheese,dinde,cheese,saumon avocat,saumon,avocat,thon,avocat,thon,surimi,thon cuit,saumon cheese,concombre cheese,concombre', 'Les Ice Cubes FAST SUSHI.'),
(23, 1, 1, 'Sushis', '<p>Composés de riz vinaigrés et de poissons crus,</p>\r\n<p>laissez-vous tenter par les Sushis FAST SUSHI :</p>\r\n<ul><li>Saumon, Saumon Avocat, Saumon Cheese,</li>\r\n<li>Daurade, Thon, Crevette, Saint-Jacques,</li>\r\n<li>Avocat, Omelette, ...</li>\r\n</ul>', 'sushis', 'Les Sushis FAST SUSHI.', 'sushis,sushi,saumon,saumon avocat,avocat,daurade,thon,crevette,saumon cheese,cheese,saumon fumé cheese,saumon fumé,omelette,saint-jacques', 'Les Sushis FAST SUSHI.'),
(24, 1, 1, 'Sushis Tartares', '<p>Composés de riz vinaigrés et de poissons crus,</p>\r\n<p>laissez-vous tenter par les Sushis Tartares FAST SUSHI :</p>\r\n<ul><li>Tartare Thon,</li>\r\n<li>Tartare Saumon.</li>\r\n</ul>', 'sushis-tartares', 'Les Sushis Tartares FAST SUSHI.', 'sushi,sushi tartare,tartare,thon,saumon,tartare thon,tartare saumon', 'Les Sushis Tartares FAST SUSHI.'),
(25, 1, 1, 'Yakitoris', '<p>Traditionnellement à base de poulet, découvrez nos</p>\r\n<p>brochettes revisitées :</p>\r\n<ul><li>Boulettes de Boeuf, Boeuf au Fromage,</li>\r\n<li>Saumon, Gambas, Saint-Jacques, Thon,</li>\r\n<li>et la classique au Poulet.</li>\r\n</ul>', 'yakitoris', 'Les Yakitoris FAST SUSHI.', 'yakitori,poulet,boulettes boeuf,boulettes,boeuf,saumon,gambas,boeuf fromage,fromage,saint-jacques,thon', 'Les Yakitoris FAST SUSHI.'),
(26, 1, 1, 'Aibiki - 3 pers.', '<p>Les Menus Aibiki pour 3 personnes FAST SUSHI sont</p>\r\n<p>accompagnés de :</p>\r\n<ul><li><strong>3 Soupes Miso </strong></li>\r\n<li><strong>3 Riz Natures</strong><strong> </strong><span style="text-decoration:underline;">OU</span> <strong>3 Salades de Choux</strong></li>\r\n</ul>', 'aibiki', 'Les Menus Aibiki FAST SUSHI.', 'aibiki,menu,aibiki 3 pers,menu 3 pers,sushi,sashimi,maki,yakitori,saumon avocat,thon mayonnaise,saumon,thon,daurade,crevette,anguile,poulet,boeuf fromage,boeuf,mayonnaise,avocat', 'Les Menus Aibiki FAST SUSHI.'),
(27, 1, 1, 'Gourmands', '<p>Les Menus Gourmands FAST SUSHI sont</p>\r\n<p>accompagnés de :</p>\r\n<ul><li><strong>1 soupe Miso</strong></li>\r\n<li><strong>1 Salade de Choux </strong><span style="text-decoration:underline;">OU</span> <strong>1 Riz Nature</strong></li>\r\n</ul>', 'gourmands', 'Les Menus Gourmands FAST SUSHI.', 'gourmand,menu,menu gourmand,yakitori,sushi,california,maki,spring rolls,spring,rolls,saumon,poulet,boeuf,boeuf fromage,fromage,boulettes poulet', 'Les Menus Gourmands FAST SUSHI.'),
(28, 1, 1, 'Kikujiro - 2 pers', '<p>Les Menus Kikujiro pour 2 personnes FAST SUSHI sont</p>\r\n<p>accompagnés de :</p>\r\n<ul><li><strong>2 Soupes Miso</strong></li>\r\n<li><strong>2 Riz Natures </strong><span style="text-decoration:underline;">OU</span> <strong>2 Salades de Choux</strong></li>\r\n</ul>', 'kikujiro', 'Les Menus Kikujiro pour 2 personnes FAST SUSHI.', 'maki,sushi,sashimi,yakitori,concombre,surimi avocat,saumon,thon,daurade,poulet,boeuf fromage,boulettes poulet,maki concombre,maki surimi avocat,sushi saumon,sushi thon,sashimi thon,sashimi saumon,yakitori boeuf fromage,fromage', 'Les Menus Kikujiro pour 2 personnes FAST SUSHI.'),
(29, 1, 1, 'Midi', '<p>Les Menus Midi FAST SUSHI sont servis de <strong>11h00 à 14h00,</strong></p>\r\n<p>accompagnés de :</p>\r\n<ul><li><strong>1 Salade de Choux</strong></li>\r\n<li><strong>1 Soupe Miso </strong><span style="text-decoration:underline;">OU</span><strong> </strong><strong>1 Riz Nature</strong></li>\r\n</ul>', 'midi', 'Les Menus Midi FAST SUSHI.', 'sushi,california,yakitori,spring rolls,sashimi,maki,saumon,thon,saumon cheese,boeuf fromage,boeuf,poulet,concombre,sushi saumon,sushi thon,california saumon,yakitori poulet,yakitori boeuf,spring rolls saumon,sashimi thon,maki saumon,maki concombre', 'Les Menus Midi FAST SUSHI.'),
(30, 1, 1, 'Plateaux', '<p>Les Menus Plateaux FAST SUSHI sont accompagnés de :</p>\r\n<ul><li><strong>1 Salade de Choux,</strong></li>\r\n<li><strong>1 Soupe Miso.</strong></li>\r\n</ul>', 'plateaux', 'Les Menus Plateaux FAST SUSHI.', 'maki,sushi,yakitori,sashimi,california,saumon,saumon avocat,avocat,thon,daurade,crevette,poulet,boeuf,boeuf fromage,thon mayonnaise,maki saumon,maki saumon  avocat,sushi saumon,sushi thon,sashimi crevette,yakitori poulet,yakiro boeuf,california thon', 'Les Menus Plateaux FAST SUSHI.'),
(31, 1, 1, 'Shogun - 4 pers', '<p>Les Menus Shogun pour 4 personnes FAST SUSHI sont</p>\r\n<p>accompagnés de :</p>\r\n<ul><li><strong>4 Soupes Miso</strong></li>\r\n<li><strong>4 Salades de Choux </strong><span style="text-decoration:underline;">OU</span><strong> 4 Riz Natures</strong></li>\r\n</ul>', 'shogun-4-pers', 'Les Menus Shogun pour 4 personnes FAST SUSHI.', 'california,maki,sushi,saumon,surimi,thon mayonnaise,thon,concombre,crevette,omelette,anguille,sushi saumon,sushi thon,sushi omelette,sushi anguille,maki saumon,maki thon,maki surimi,maki concombre,california thon,california saumon,california surimi', 'Les Menus Shogun pour 4 personnes FAST SUSHI.'),
(32, 1, 1, 'Yakitoris', '<p>Les Menus Yakitori FAST SUSHI sont servis de</p>\r\n<p><strong>11h00 à 14h00</strong>, accompagnés de :</p>\r\n<ul><li><strong>1 Riz Nature</strong></li>\r\n<li><strong>1 Soupe Miso</strong></li>\r\n<li><strong>1 Salade de Choux</strong></li>\r\n</ul>', 'yakitoris', 'Les Menus Yakitori FAST SUSHI.', 'menu,yakitori,menu yakitori,poulet,boulettes boeuf,boulettes,boeuf,saumon,gambas,boeuf fromage,fromage,thon,saint-jacques', 'Les Menus Yakitori FAST SUSHI.'),
(33, 1, 1, 'Tartares', '<p>Nos poissons SUSHI FAST finement découpés aux différents assaisonnements : </p>\r\n<ul><li>Daurade,</li>\r\n<li>Saumon</li>\r\n<li>Chirashi mariné.</li>\r\n</ul>', 'tartares', 'Tartares', 'tartares,daurade,saumon,chirashi marine,chirashi,mangue,avocat,oeufs de saumon,thon,poireau,concombre,coriandre,citron,aneth,mayonnaise', 'Les Tartares SUSHI FAST.'),
(34, 1, 1, 'Avocat Makis', '<p>L''avocat autour du riz et du vinaigre de riz</p>\r\n<p>sucré composent de savoureux Avocat Makis FAST SUSHI :</p>\r\n<ul><li>Crevette, Saumon, Thon cuit,</li>\r\n<li>Saumon fumé cheese.</li>\r\n</ul>', 'avocat-makis', 'Avocat Makis Sushi Fast.', 'avocat maki,avocat,maki,riz,vinaigre riz,crevette,saumon,thon,thon cuit,saumon fume cheese,saumon fume,saumon,cheese,fromage,saumon cheese,saumon fromage,saumon fume fromage', 'Les Avocat Makis Sushi Fast.'),
(35, 1, 1, 'Boissons', '<p>Les Boissons proposées pas SUSHI FAST :</p>\r\n<ul><li>Soda, Jus de fruits,</li>\r\n<li>Thé glacé, Limonade,</li>\r\n<li>Eau pétillante, Eau plate...</li>\r\n</ul>', 'boissons', 'Boissons SUSHI FAST.', 'boissons,coca cola,coca cola zero,orangina,oasis,tropico,minute maid,ice tea,jus de fruits,perrier,eau minerale,boisson petillante,eau petillante,litchi,fraise,pomme,peche,kiwi,bouteille,canette', 'Les Boissons SUSHI FAST.'),
(36, 1, 1, 'Desserts', '<p>Les Desserts proposés par SUSHI FAST :</p>\r\n<ul><li>Brownies, Beignets, Fondant au chocolat,</li>\r\n<li>Glace Häagen-Dazs,</li>\r\n<li>Perle de coco, Tiramisu, Maki choc...</li>\r\n</ul>', 'desserts', 'Desserts SUSHI FAST.', 'dessert,brownie,beignet,beignet pomme,eignet chocolat,perle de coco,tiramisu,fondant chocolat,maki choc nutella,maki choc nutella banane,glace,glace haagen dazs', 'Les Desserts SUSHI FAST.'),
(37, 1, 1, 'Enfant', '<p>Le Menu Enfant SUSHI FAST.</p>', 'enfant', 'Menu Enfant Sushi Fast', 'menu enfant sushi fast,menu enfant,ice cube poulet mayonnaise,ice cube poulet,ice cube,poulet mayonnaise,poulet,yakitori boulettes poulet,brochette boulettes poulet,yakitori,brochette,boulettes poulet,poulet,jus de fruit,kinder surprise,kinder', 'Les Menus Enfant SUSHI FAST.');
